def souradnice_vypocet(souradnice, okolik):
    if okolik[0] == "R":
        for i in range(0, okolik[1]):
            souradnice[0] = souradnice[0] + 1
            cesta11.append([souradnice[0], souradnice[1]])
    elif okolik[0] == "L":
        for i in range(0, okolik[1]):
            souradnice[0] = souradnice[0] - 1
            cesta11.append([souradnice[0], souradnice[1]])
    elif okolik[0] == "U":
        for i in range(0, okolik[1]):
            souradnice[1] = souradnice[1] + 1
            cesta11.append([souradnice[0], souradnice[1]])
    elif okolik[0] == "D":
        for i in range(0, okolik[1]):
            souradnice[1] = souradnice[1] - 1
            cesta11.append([souradnice[0], souradnice[1]])

    return souradnice, cesta11

